"use strict";

/**
 * Example JavaScript code that interacts with the page and Web3 wallets
 */

// Unpkg imports
const Web3Modal = window.Web3Modal.default;
const WalletConnectProvider = window.WalletConnectProvider.default;
const Fortmatic = window.Fortmatic;
const evmChains = window.evmChains;

// Web3modal instance
let web3Modal;

// Chosen wallet provider given by the dialog window
let provider;


// Address of the selected account
let selectedAccount;

const usdtContractTestnet = '0xd60B491E4f33D71d974b033B9328f5c0504b7B88';
const usdtContractMainnet = '0xdAC17F958D2ee523a2206206994597C13D831ec7';
var usdtContractAddress;

const kpgContractTestnet = '0xeccd68E23CA0D0DD2184F20Db728BA08339FE602';
const kpgContractMainnet = '0x836F91Ab1aE172958E494e8407B0F88d05166A03';
var kpgContractAddress;
/**
 * Setup the orchestra
 */
function init() {

	// console.log("Initializing example");
	// console.log("WalletConnectProvider is", WalletConnectProvider);
	// console.log("window.web3 is", window.web3, "window.ethereum is", window.ethereum);
	var isBetamax;
	// Check that the web page is run in a secure context,
	// as otherwise MetaMask won't be available


	if (window.location.hostname.includes('staging') || window.location.hostname.includes('ngrok')) {
		usdtContractAddress = usdtContractTestnet;
		kpgContractAddress = kpgContractTestnet;
	} else {
		usdtContractAddress = usdtContractMainnet;
		kpgContractAddress = kpgContractMainnet;
	}

	if (location.protocol !== 'https:') {
		// https://ethereum.stackexchange.com/a/62217/620
		const alert = document.querySelector(".non-https-alert");
		alert.style.display = "block";
		document.querySelector("#btn-connect").setAttribute("disabled", "disabled");
		return;
	}

	// Tell Web3modal what providers we have available.
	// Built-in web browser provider (only one can exist as a time)
	// like MetaMask, Brave or Opera is added automatically by Web3modal
	const providerOptions = {
		walletconnect: {
			package: WalletConnectProvider,
			options: {
				// Mikko's test key - don't copy as your mileage may vary
				rpc: {
					43113: "https://api.avax-test.network/ext/bc/C/rpc",
				}
			}
		},
	};

	web3Modal = new Web3Modal({
		cacheProvider: true, // optional
		providerOptions, // required
		disableInjectedProvider: false, // optional. For MetaMask / Brave / Opera.
	});

	isBetamax = sessionStorage.getItem('isBetamax');

	isBetamax ? null : checkProvider();

	//console.log("Web3Modal instance is", web3Modal);
}


/**
 * Kick in the UI action after Web3modal dialog has chosen a provider
 */

async function checkProvider() {
	var address = sessionStorage.getItem('addresses');
	if (address) {

		try {
			provider = await web3Modal.connect();

		} catch (e) {
			// console.log("Could not get a wallet connection", e);
			return;
		}
	} else {

	}

}

